autohoh={interFace:"",setup:function(){this.interFace+="<span>Autopraise: <a href=\"#\" id=\"autohoh_praise\" onclick=\"autohoh.toggle('praise')\">off</a></span><br>";this.interFace+="<span>Autohunt: <a href=\"#\" id=\"autohoh_hunt\" onclick=\"autohoh.toggle('hunt')\">off</a></span><br>";this.interFace+="<span>Autocraft: <a href=\"#\" onclick=\"autohoh.editCrafting()\">edit</a></span>";var rightTabContent=document.getElementById("rightTabLog");rightTabContent.innerHTML=this.interFace+rightTabContent.innerHTML;var loop=setInterval(function(){if(autohoh.automation.praise){game.religion.praise()};if(autohoh.automation.hunt){document.getElementById("fastHuntContainer").childNodes[1].click()};var craftingTable=document.getElementById("craftContainer").childNodes[0];for(var i=0;i<autohoh.craftList.length;i+=1){for(var j=0;j<craftingTable.childNodes.length;j+=1){if(autohoh.craftList[i]===craftingTable.childNodes[j].childNodes[0].innerHTML.substring(0,autohoh.craftList[i].length)){craftingTable.childNodes[j].childNodes[5].childNodes[0].click();break}}}},10000);},automation:{praise:false,hunt:false},toggle:function(thing){if(this.automation[thing]){this.automation[thing]=false;document.getElementById("autohoh_"+thing).innerHTML="off"}else{this.automation[thing]=true;document.getElementById("autohoh_"+thing).innerHTML="on"}},craftList:[],editCrafting:function(){var userInput=prompt("Write a list of resources separated by commas in the order you want them crafted.\nExample: steel,plate,eludium\nCurrent: "+this.craftList);if(userInput!=null){autohoh.craftList=userInput.split(",")}}};autohoh.setup();
